    public function __construct(
        private MailerInterface $mailer,
        private UrlGeneratorInterface $router,
        private UserRepository $userRepository,
    ) {
        $this->fromEmail = $_ENV['MAILER_FROM_ADDRESS'];
        $this->fromName = $_ENV['MAILER_FROM_NAME'];
    }

    public function generateUrl(string $route, array $params = []): string
    {
        return $this->router->generate($route, $params, UrlGeneratorInterface::ABSOLUTE_URL);
    }

    /**
     * Try to get the email of someone from the collectivite
     *
     * @return string
     */
    public function getEmailCollectivite(Collectivite $collectivite): string
    {
        $user = $this->userRepository->findOneBy(['collectivite' => $collectivite, 'adminCollectivite' => true]);
        return $user->getEmail();
    }

